import os
import requests

os.makedirs("data/input/", exist_ok=True)

with open("data/input/list.txt", "w") as list_fw:
    for sheet_name, url in urls.items():
        extension = url.split(".")[-1]
        print("Downloading sheet #%s" % sheet_name)
        r = requests.get(url)
        filepath = "data/input/%s.%s" % (sheet_name, extension)
        with open(filepath, 'wb') as img_fw:
            img_fw.write(r.content) 

        list_fw.write("%s.%s,%s\n" % (sheet_name, extension, sheet_name))